def openjson(x):
    while True:
        if os.path.exists('{}/{}.json'.format(x, x)) == True:
            with open('{}/{}.json'.format(x, x), 'r', encoding='utf-8') as json_file:
                shezhi = json.load(json_file)
                return shezhi
        else:
            shutil.copy('shezhi/shezhi.json', '{}/{}.json'.format(x, x))
            continue



