def calculate_color_difference(region_start, target_color):
    x, y,x1,y1 = region_start
    region = piptu[y:y+y1, x:x+x1]

    # 计算与目标颜色的差异
    difference = np.sqrt(np.sum((region.astype("float") - target_color) ** 2, axis=2))

    # 计算平均差异
    average_difference = np.mean(difference)
    if average_difference <= 5:
        return True
    else:
        return

