def enum_child_windows(parent_handle):
    """
    列出指定窗口句柄的所有子窗口句柄。
    :param parent_handle: 父窗口句柄
    :return: None
    """
    child_handles = []
    def callback(hwnd, extra):
        child_handles.append(hwnd)
    win32gui.EnumChildWindows(parent_handle, callback, None)
    return child_handles




