def xycolor(leixing, tolerance = 20, percent = 0.3):
    if leixing == 'rwh':
        target_color = (3,11,94)
        x = 496
        y = 2
    if leixing == 'pb':
        target_color = (243,235,198)
        x = 24
        y = 174
    if leixing == 'pb1':
        target_color = (243,235,198)
        x = 109
        y = 253
    if leixing == 'rwl':
        target_color = (198,176,164)
        x = 496
        y = 7
    if leixing == 'bb':
        target_color = (198,176,164)
        x = 430
        y = 3
    x_end = min(x + 3, piptu.shape[1])
    y_end = min(y + 3, piptu.shape[0])
    match_count = 0
    for y in range(y, y_end):
        for x in range(x, x_end):
            color_diff = np.abs(piptu[y, x] - np.array(target_color))
            if np.all(color_diff <= tolerance):
                match_count += 1
    total_pixels = (x_end - x) * (y_end - y)
    return (match_count / total_pixels) >= percent


