# 创建并启动截图线程
def remove_close_matches(matches, threshold):
    if len(matches) == 0:
        return matches
    # 将列表转换为NumPy数组以便操作
    matches = np.array(matches)
    # 按x坐标排序
    sorted_matches = matches[np.argsort(matches[:, 0])]
    # 保留距离较远的坐标
    filtered_matches = [sorted_matches[0]]
    for current in sorted_matches[1:]:
        # 计算当前坐标与已保存坐标的距离
        distances = np.sqrt(np.sum((filtered_matches - current) ** 2, axis=1))
        # 如果所有距离都大于阈值，则保存当前坐标
        if np.all(distances > threshold):
            filtered_matches.append(current)
    x = filtered_matches[0][0]
    y = filtered_matches[0][1]
    return x, y


def is_similar(coord1, coord2, threshold=5):
    # 计算两个坐标之间的差值
    x_diff = abs(coord1[0] - coord2[0])
    y_diff = abs(coord1[1] - coord2[1])
    return x_diff <= threshold and y_diff <= threshold


def remove_close_pointsall(matches, threshold=10):
    if len(matches) == 0:
        return matches
    matches = np.array(matches)
    sorted_matches = matches[np.argsort(matches[:, 0])]
    filtered_matches = [sorted_matches[0]]
    for current in sorted_matches[1:]:
        distances = np.sqrt(np.sum((filtered_matches - current) ** 2, axis=1))
        if np.all(distances > threshold):
            filtered_matches.append(current)
    return filtered_matches


