def locateOnScreen(imgname, threshold=0.8):
    
    image_data = base64.b64decode(data[imgname])
    nparr = np.frombuffer(image_data, np.uint8)
    img = cv2.imdecode(nparr, cv2.IMREAD_COLOR)
    w, h = img.shape[:2]
    main_image = piptu
    res = cv2.matchTemplate(main_image, img, cv2.TM_CCOEFF_NORMED)
    loc = np.where(res >= threshold)
    pt1 = []
    for pt in zip(*loc[::-1]):
        center_x = pt[0] + w // 2
        center_y = pt[1] + h // 2
        pt1.append((center_x, center_y))
    if pt1 == []:
        return None, None
    unique_centers = []
    for center in pt1:
        if all(not is_similar(center, unique_center) for unique_center in unique_centers):
            unique_centers.append(center)
    return unique_centers[0][0], unique_centers[0][1]


